sys_script_include.script:
var SharepointOnlineOAuthToken = Class.create();
SharepointOnlineOAuthToken.prototype = Object.extendsObject(global.OAuthUtil, {
	preprocessAccessToken: function (requestParamMap) {
        this.oauthContext.addHeader(
            'Content-Type',
            'application/x-www-form-urlencoded'
        );
        var scope = requestParamMap.get('scope');
        requestParamMap.put('resource', scope);
		delete requestParamMap['scope'];
    },

    type: 'SharepointOnlineOAuthToken'
});
